import VendorSideBar from "@/components/vendor/Sidebar";
import VendorTopBar from "@/components/vendor/TopBar";
import Head from "next/head";
import { Dialog, Tab, Transition } from "@headlessui/react";
import { Fragment, useEffect, useRef, useState } from "react";
import UploadProfile from "@/components/UploadProfile";
import FormField from "@/components/FormField";
import Button from "@/components/Button";
import { useSession } from "next-auth/react";
import { axiosInstanceAuth } from "@/pages/api/axiosApi";
import { useRouter } from "next/router";

export default function VendorSettings() {
	const router = useRouter();
	const { vrefid } = router.query;

	const cancelButtonRef = useRef(null);
	const [changePass, setChangePass] = useState(false);
	const tabHeading_1 = [
		{
			title: "Profile"
		},
		{
			title: "Agreement"
		}
	];
	const { data: session } = useSession();
	const [token, settoken] = useState("");
	const [vdata, setvdata] = useState({});

	useEffect(() => {
		if (session) {
			settoken(session.accessToken as string);
		} else if (!session) {
			settoken("");
		}
	}, [session]);

	const axiosInstanceAuth2 = axiosInstanceAuth(token);

	const [email, setemail] = useState("");
	const [cname, setcname] = useState("");
	const [phone, setphone] = useState("");
	const [aname, setaname] = useState("");
	const [ophone, setophone] = useState("");
	const [lno, setlno] = useState("");
	const [add, setadd] = useState("");
	const [sdate, setsdate] = useState("");
	const [edate, setedate] = useState("");
	const [aggrement, setaggrement] = useState("");
	// const [sign, setsign] = useState<File | null>(null);
	// const [check1, setcheck1] = useState(false);
	// const [file, setfile] = useState(false);

	async function loadVendorData(id: string) {
		await axiosInstanceAuth2
			.get(`/vendors/vendor_data/${id}/`)
			.then(async (res) => {
				console.log("!", res.data);
				if (res.data && res.data.length > 0) {
					setvdata(res.data[0]);
					const data = res.data;
					for (let i = 0; i < data.length; i++) {
						setemail(data[i]["email"]);
						setcname(data[i]["company_name"]);
						setphone(data[i]["contact_number"]);
						setaname(data[i]["agent_name"]);
						// setmsg(data[i]["message"]);
						setsdate(data[i]["agreement_valid_start_date"]);
						setedate(data[i]["agreement_valid_end_date"]);
						setaggrement(data[i]["agreement"]);
					}
				}
			})
			.catch((err) => {
				console.log("!", err);
			});
	}

	async function loadVendorData2(id: string) {
		await axiosInstanceAuth2
			.get(`/vendors/vendor_data2/${id}/`)
			.then(async (res) => {
				console.log("!", res.data);
				if (res.data && res.data.length > 0) {
					setvdata(res.data[0]);
					const data = res.data;
					for (let i = 0; i < data.length; i++) {
						setlno(data[i]["license_number"]);
						setophone(data[i]["contact_2"]);
						setadd(data[i]["headquater_address"]);
					}
				}
			})
			.catch((err) => {
				console.log("!", err);
			});
	}

	useEffect(() => {
		if (vrefid && vrefid.length > 0 && token && token.length > 0) {
			loadVendorData(vrefid);
			loadVendorData2(vrefid);
		}
	}, [vrefid, token]);

	return (
		<>
			<Head>
				<title>Vendor | Settings</title>
				<meta name="description" content="Generated by create next app" />
			</Head>
			<main>
				<VendorSideBar />
				<VendorTopBar />
				<div
					id="overlay"
					className="fixed left-0 top-0 z-[9] hidden h-full w-full bg-[rgba(0,0,0,0.2)] dark:bg-[rgba(255,255,255,0.2)]"
				></div>
				<div className="layoutWrap p-4 lg:p-8">
					<div className="relative rounded-normal bg-white shadow-normal dark:bg-gray-800">
						<h1 className="mb-3 p-10 pb-0 text-2xl font-bold">Settings</h1>
						<Tab.Group>
							<div className="border-b dark:border-b-gray-600">
								<Tab.List className={"mx-auto w-full max-w-[1150px] px-4"}>
									{tabHeading_1.map((item, i) => (
										<Tab key={i} as={Fragment}>
											{({ selected }) => (
												<button
													className={
														"border-b-4 py-3 px-6 font-semibold focus:outline-none" +
														" " +
														(selected
															? "border-primary text-primary"
															: "border-transparent text-darkGray dark:text-gray-400")
													}
												>
													{item.title}
												</button>
											)}
										</Tab>
									))}
								</Tab.List>
							</div>
							<Tab.Panels>
								<Tab.Panel>
									<div className="mx-auto w-full max-w-[1150px] px-4 py-6">
										<div className="mb-4">
											<UploadProfile note="Supported Formats 2 mb  : Png , Jpeg" />
										</div>
										<div className="-mx-3 flex flex-wrap">
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="Company Name"
													fieldType="input"
													inputType="text"
													value={cname}
													handleChange={(e) => setcname(e.target.value)}
													readOnly
												/>
											</div>
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="Email"
													fieldType="input"
													inputType="email"
													readOnly
													value={email}
													handleChange={(e) => setemail(e.target.value)}
												/>
											</div>
										</div>
										<FormField
											label="Agent Name"
											fieldType="input"
											inputType="text"
											value={aname}
											handleChange={(e) => setaname(e.target.value)}
											readOnly
										/>
										<div className="-mx-3 flex flex-wrap">
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="Contact Number"
													fieldType="input"
													inputType="number"
													value={phone}
													handleChange={(e) => setphone(e.target.value)}
													readOnly
												/>
											</div>
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="Contact Number (Optional)"
													fieldType="input"
													inputType="number"
													value={ophone}
													handleChange={(e) => setophone(e.target.value)}
													readOnly
												/>
											</div>
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="License Number"
													fieldType="input"
													inputType="text"
													value={lno}
													handleChange={(e) => setlno(e.target.value)}
													readOnly
												/>
											</div>
											<div className="mb-4 w-full px-3 md:max-w-[50%]">
												<FormField
													label="Headquarter Address"
													fieldType="input"
													inputType="text"
													readOnly
													value={add}
													handleChange={(e) => setadd(e.target.value)}
												/>
											</div>
										</div>
										<hr className="my-4" />
										<div>
											<h6 className="mb-1 font-bold">Password Settings</h6>
											<Button btnType="button" label="Change Password" handleClick={() => setChangePass(true)} />
										</div>
									</div>
								</Tab.Panel>
								<Tab.Panel>
									<div className="mx-auto w-full max-w-[1150px] px-4 py-6">
										{aggrement && aggrement.length > 0 && (
											<iframe src={`${aggrement}`} className="h-[100vh] w-[100%]"></iframe>
										)}
										<div className="flex w-full flex-wrap md:max-w-[50%]">
											{/* <h6 className="mb-2 w-full font-bold">Agreement Validity</h6> */}
											<div className="w-full pr-2 md:max-w-[50%]">
												<FormField
													label="Agreement Start Date"
													fieldType="input"
													inputType="date"
													value={sdate}
													handleChange={(e) => {
														setsdate(e.target.value);
													}}
													readOnly
												/>
											</div>
											<div className="w-full pl-2 md:max-w-[50%]">
												<FormField
													label="Agreement Start Date"
													fieldType="input"
													inputType="date"
													value={edate}
													handleChange={(e) => {
														setedate(e.target.value);
													}}
													readOnly
												/>
											</div>
										</div>
									</div>
								</Tab.Panel>
							</Tab.Panels>
						</Tab.Group>
					</div>
				</div>
			</main>
			<Transition.Root show={changePass} as={Fragment}>
				<Dialog as="div" className="relative z-40" initialFocus={cancelButtonRef} onClose={setChangePass}>
					<Transition.Child
						as={Fragment}
						enter="ease-out duration-300"
						enterFrom="opacity-0"
						enterTo="opacity-100"
						leave="ease-in duration-200"
						leaveFrom="opacity-100"
						leaveTo="opacity-0"
					>
						<div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" />
					</Transition.Child>

					<div className="fixed inset-0 z-10 overflow-y-auto">
						<div className="flex min-h-full items-end justify-center p-4 text-center sm:items-center">
							<Transition.Child
								as={Fragment}
								enter="ease-out duration-300"
								enterFrom="opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95"
								enterTo="opacity-100 translate-y-0 sm:scale-100"
								leave="ease-in duration-200"
								leaveFrom="opacity-100 translate-y-0 sm:scale-100"
								leaveTo="opacity-0 translate-y-4 sm:translate-y-0 sm:scale-95"
							>
								<Dialog.Panel className="relative w-full transform overflow-hidden rounded-[30px] bg-[#FBF9FF] text-left text-black shadow-xl transition-all dark:bg-gray-800 dark:text-white sm:my-8 sm:max-w-lg">
									<div className="flex items-center justify-between bg-gradient-to-b from-gradLightBlue to-gradDarkBlue px-8 py-3 text-white">
										<h4 className="flex items-center font-semibold leading-none">Change Password</h4>
										<button
											type="button"
											className="leading-none hover:text-gray-700"
											onClick={() => setChangePass(false)}
										>
											<i className="fa-solid fa-xmark"></i>
										</button>
									</div>
									<div className="p-8">
										<FormField fieldType="input" inputType="password" label="Old Password" required />
										<FormField fieldType="input" inputType="password" label="New Password" required />
										<FormField fieldType="input" inputType="password" label="Confirm Password" required />
										<div className="text-center">
											<Button label="Submit" />
										</div>
									</div>
								</Dialog.Panel>
							</Transition.Child>
						</div>
					</div>
				</Dialog>
			</Transition.Root>
		</>
	);
}
